  const data = { k1: 'v1' };
  const state = {};
</script>
{#each
  ['k1'].map(key => {
    const val = data[key];
    console.log('step'); // should be called only once

    //state[key] = val;
    // this global assignment makes the each-expression impure
    // and causes an infinite loop in the svelte runtime
    // 'step' is printed over and over in the browser console

    return [key, val];
  }) as [key, val]
}
  <div>{key}: {val}</div>
{/each}
